import React, { useEffect, useRef } from 'react';
import * as d3 from "d3";

export function BarChart({csvdata}){

    const svgRef = useRef();
    const wrapperRef = useRef();

    const clearBoard=()=>{
        const accessToRef = d3.select(svgRef.current)
        accessToRef.selectAll("svg > *").remove();
    }

    const horizontal = (data) => {

        // Define the chart dimensions
        const width = 900;
        const height = 600;
        // D3 code here
        const svg = d3.select(svgRef.current)
                        .attr("height",height)
                        .attr("width",width)
                        .style("background-color","white");

        // Add X axis
        var x = d3.scaleLinear()
        .domain([0, 13000])
        .range([ 0, width]);
        svg.append("g")
        .attr("transform", "translate(0," + height + ")")
        .call(d3.axisBottom(x))
        .selectAll("text")
            .attr("transform", "translate(-10,0)rotate(-45)")
            .style("text-anchor", "end");

        // Y axis
        var y = d3.scaleBand()
        .range([ 0, height ])
        .domain(data.map(function(d) { return d.country; }))
        .padding(.1);
        svg.append("g")
        .call(d3.axisLeft(y))

        //Bars
        svg.selectAll("myRect")
        .data(data)
        .enter()
        .append("rect")
        .attr("x", x(0) )
        .attr("y", function(d) { return y(d.country); })
        .attr("width", function(d) { return x(d.expectancy); })
        .attr("height", y.bandwidth() )
        .attr("fill", "#69b3a2")



    }



    useEffect(()=>{
        console.log("here")
        clearBoard();
        let data = []
        for(var i=0;i<csvdata.country.length;i++){
            let obj = {};
            obj.country = csvdata.country[i];
            obj.expectancy = csvdata.expectancy[i]-20;
            data.push(obj);
        }
        horizontal(data);
        // draw(data);

    },[csvdata])

    return (
        <div ref={wrapperRef}>
          <svg ref={svgRef} style={{width:"900px",height:"600px"}}>
          </svg>
        </div>
      );
}
