import React, { useEffect, useRef } from 'react';
import * as d3 from "d3";
import axios from 'axios';

export function DensityPlot({csvdata}){

    const svgRef = useRef();
    const wrapperRef = useRef();

    const clearBoard=()=>{
        const accessToRef = d3.select(svgRef.current)
        accessToRef.selectAll("svg > *").remove();
    }

    // Function to compute density
    function kernelDensityEstimator(kernel, X) {
        return function(V) {
        return X.map(function(x) {
            return [x, d3.mean(V, function(v) { return kernel(x - v); })];
        });
        };
    }
    function kernelEpanechnikov(k) {
        return function(v) {
        return Math.abs(v /= k) <= 1 ? 0.75 * (1 - v * v) / k : 0;
        };
    }

    const draw= (data1,data2) => {


        // D3 code here
        var margin = {top: 20, right: 30, bottom: 40, left: 90},
            width = 460 - margin.left - margin.right,
            height = 400 - margin.top - margin.bottom;

        // append the svg object to the body of the page
        var svg = d3.select(svgRef.current)
            .attr("width", width + margin.left + margin.right)
            .attr("height", height + margin.top + margin.bottom)
        .append("g")
            .attr("transform",
                "translate(" + margin.left + "," + margin.top + ")");

        // add the x Axis
        var x = d3.scaleLinear()
            .domain([0, 200])
            .range([0, width]);
            svg.append("g")
            .attr("transform", "translate(0," + height + ")")
            .call(d3.axisBottom(x));

        // add the y Axis
        var y = d3.scaleLinear()
            .range([height, 0])
            .domain([0, 10]);
            svg.append("g")
            .call(d3.axisLeft(y));

        // Compute kernel density estimation
        var kde = kernelDensityEstimator(kernelEpanechnikov(7), x.ticks(60))
        var density1 =  kde( data1.map(function(d){  return d.alcohol; }) )

        var density2 =  kde( data2.map(function(d){  return d.expenditure; }) )


        // Plot the area
            svg.append("path")
                .attr("class", "mypath")
                .datum(density1)
                .attr("fill", "#69b3a2")
                .attr("opacity", ".6")
                .attr("stroke", "#000")
                .attr("stroke-width", 1)
                .attr("stroke-linejoin", "round")
                .attr("d",  d3.line()
                        .curve(d3.curveBasis)
                            .x(function(d) { return x(d[0]); })
                            .y(function(d) { return y(d[1]); })
                );

            // Plot the area
            svg.append("path")
            .attr("class", "mypath")
            .datum(density2)
            .attr("fill", "#404080")
            .attr("opacity", ".6")
            .attr("stroke", "#000")
            .attr("stroke-width", 1)
            .attr("stroke-linejoin", "round")
            .attr("d",  d3.line()
                    .curve(d3.curveBasis)
                        .x(function(d) { return x(d[0]); })
                        .y(function(d) { return y(d[1]); })
            );





    }



    useEffect(()=>{

        let tmpData1 = []
        let tmpData2 = []
        axios.get('http://localhost:8000/pcp').then((repos) => {
                const allRepos = repos.data;
                let parsedResponse = JSON.parse(allRepos);
                let tempState = parsedResponse;
                console.log("data",tempState)
                tempState.forEach(ele => {
                    let obj1 = {};
                    obj1.alcohol = ele.Alcohol;
                    obj1.expectancy = ele['Life Expectancy'];

                    let obj2 = {}
                    obj2.expenditure = ele['Total expenditure'];
                    obj2.expectancy = ele['Life Expectancy'];
                    tmpData1.push(obj1);
                    tmpData2.push(obj2);
                });
                clearBoard();
                draw(tmpData1,tmpData2);
            });

        // draw(data);

    },[csvdata])

    return (
        <div ref={wrapperRef}>
          <svg ref={svgRef} style={{width:"500px",height:"400px"}}>
          </svg>
        </div>
      );
}
