import React, { useEffect, useRef } from 'react';
import * as d3 from "d3";

export function BarChart({csvdata}){

    const svgRef = useRef();
    const wrapperRef = useRef();

    const clearBoard=()=>{
        const accessToRef = d3.select(svgRef.current)
        accessToRef.selectAll("svg > *").remove();
    }

    const horizontal = (data) => {

        // Define the chart dimensions
        const width = 900;
        const height = 600;
        // D3 code here
        const svg = d3.select(svgRef.current)
                        .attr("height",height)
                        .attr("width",width)
                        .style("background-color","white");



    }



    useEffect(()=>{
        console.log("here")
        clearBoard();
        let data = []
        for(var i=0;i<csvdata.country.length;i++){
            let obj = {};
            obj.country = csvdata.country[i];
            obj.expectancy = csvdata.expectancy[i]-20;
            data.push(obj);
        }
        horizontal(data);
        // draw(data);

    },[csvdata])

    return (
        <div ref={wrapperRef}>
          <svg ref={svgRef} style={{width:"900px",height:"600px"}}>
          <g className="x-axis"/>
            <g className="y-axis" />
          </svg>
        </div>
      );
}
