import { geoMercator, geoPath, max } from "d3";
import React,{ useEffect, useRef } from "react";
import {select} from "d3";

// import useResizeObserver from "./useResizeObserver";

function GeoChart({data,property,csvdata}){


    const svgRef = useRef(null);
    const wrapperRef = useRef();

    console.log("data",data.features[0])

    useEffect(()=>{

        // const accessToRef = select(svgRef.current);
        const width=800;
        const height = 550;
        var csv_country = Object.values(csvdata.country)
        var csv_pop = Object.values(csvdata.expectancy)
        console.log("why",typeof(csv_pop))
        const min_val =Math.min(csv_pop)
        const max_val = Math.max(csvdata.expectancy)

        console.log("minmax",min_val,max_val)

        const projection = geoMercator().fitSize([width,height],data);

        const pathGenerator = geoPath().projection(projection);

        // accessToRef.selectAll(".country")
        // .data(data.features)
        // .append("path")
        // .attr("class", "country")
        // .attr("d", feature => pathGenerator(feature));
        const accessToRef = select(svgRef.current)
                                    .attr("height",height)
                                    .attr("width",width)
                                    .style("background-color","red")
                                    // .attr("transform", "translate(" + margin_left + "," + margin_top + ")");
        accessToRef.selectAll(".country")
                    .data(data.features)
                    .enter()
                        .append('path')
                        .attr('class','country')
                        .attr('d', feature=> {
                            return pathGenerator(feature)
                        })


    },[data,property])


    return (
        <div ref={wrapperRef} style={{ marginBottom: "2rem" }}>
          <svg ref={svgRef}></svg>
        </div>
      );

}


export default GeoChart;
