import React, { useEffect, useRef } from 'react';
import * as d3 from "d3";

export function BarChart({csvdata}){

    const svgRef = useRef();
    const wrapperRef = useRef();

    const clearBoard=()=>{
        const accessToRef = d3.select(svgRef.current)
        accessToRef.selectAll("svg > *").remove();
    }

    const draw = (data)=>{
        // D3 code here
        const svg = d3.select(svgRef.current);

        // Define the chart dimensions
        const width = 500;
        const height = 300;

        // Create the scales for x and y axes
        const xScale = d3
        .scaleLinear()
        .domain([0, d3.max(data, (d) => d.expectancy)])
        .range([0, width]);

        const yScale = d3
        .scaleBand()
        .domain(data.map((d) => d.country))
        .range([height, 0])
        .padding(0.2);

        // Create the x and y axes
        const xAxis = d3.axisBottom().scale(xScale);
        const yAxis = d3.axisLeft().scale(yScale);

        // Draw the x and y axes
        svg.select('.x-axis').call(xAxis);
        svg.select('.y-axis').call(yAxis);

        // Draw the bars
        svg
        .selectAll('.bar')
        .data(data)
        .enter()
        .append('rect')
        .attr('class', 'bar')
        .attr('x', 0)
        .attr('y', (d) => yScale(d.country))
        .attr('width', (d) => xScale(d.expectancy))
        .attr('height', yScale.bandwidth())
        .attr('fill', 'steelblue');
    }

    useEffect(()=>{
        console.log("here")
        clearBoard();
        let data = []
        for(var i=0;i<csvdata.country.length;i++){
            let obj = {};
            obj.country = csvdata.country[i];
            obj.expectancy = csvdata.expectancy[i];
            data.push(obj);
        }
        draw(data);

    },[csvdata])

    return (
        <div ref={wrapperRef}>
          <svg ref={svgRef}>
            <g className="x-axis" />
            <g className="y-axis" />
          </svg>
        </div>
      );
}
