import { geoMercator, geoPath, max } from "d3";
import React,{ useEffect, useRef } from "react";
import {select,scaleLinear} from "d3";

// import useResizeObserver from "./useResizeObserver";

function GeoChart({data,property,csvdata}){


    const svgRef = useRef(null);
    const wrapperRef = useRef();

    console.log("data",data.features[0])

    useEffect(()=>{

        // const accessToRef = select(svgRef.current);
        const width=800;
        const height = 550;

        var min=100000
        var max = -100000

        for(var i=0;i<csvdata.expectancy.length;i++){
            if(csvdata.expectancy[i]<min){
                min = csvdata.expectancy[i];
            }
            if(csvdata.expectancy[i]>min){
                max = csvdata.expectancy[i];
            }
        }

       var colorScale = scaleLinear().domain([min,max]).range(['#ccc','blue'])

        const projection = geoMercator().fitSize([width,height],data);

        const pathGenerator = geoPath().projection(projection);

        // accessToRef.selectAll(".country")
        // .data(data.features)
        // .append("path")
        // .attr("class", "country")
        // .attr("d", feature => pathGenerator(feature));
        const accessToRef = select(svgRef.current)
                                    .attr("height",height)
                                    .attr("width",width)
                                    .style("background-color","red")
                                    // .attr("transform", "translate(" + margin_left + "," + margin_top + ")");
        accessToRef.selectAll(".country")
                    .data(data.features)
                    .enter()
                        .append('path')
                        .attr('class','country')
                        .attr('d', feature=> {
                            return pathGenerator(feature)
                        })
                        .attr("fill",feature => {
                            var req_name = feature.property.name;
                            var index = -1;
                            for(var i=0;i<csvdata.country.length;i++){
                                if(csvdata.country[i]==req_name){
                                    console.log("yes");
                                    index = i;
                                    break;
                                }
                            }
                            if(index==-1)return "white";
                            return colorScale(csvdata.expectancy[index])
                        })


    },[data,property])


    return (
        <div ref={wrapperRef} style={{ marginBottom: "2rem" }}>
          <svg ref={svgRef}></svg>
        </div>
      );

}


export default GeoChart;
